// 不死身設定
<D-Setting:Immortal>

// 自動前進禁止
<D-Setting:NoStep>

// 対象へ接近
<D-Motion:near/>

// 攻撃毎に武器振り
<D-Motion:every&attack>
motionFrame = 3 // 2*3=6フレーム
nextDelay = 4 // 繰り返し間隔
</D-Motion>

// 攻撃毎にアニメーション＆ダメージ
<D-Animation:damage>
nextDelay = 4 // 繰り返し間隔
</D-Animation>

// 戻る
<D-Motion:return/>
