// 自動前進禁止
<D-Setting:NoStep>

// 発動モーション固定（ディレイ15/60秒）
<D-Motion>
motion = item
motionPattern = 2
</D-Motion>

// 発動アニメーション
<D-Animation:spell/>

// 透明化しながら前進
<D-Motion:near&invisible>
ex += 48 * mirroring // 対象の少し手前に移動
playSe = Wind4
</D-Motion>

// 対象とすれ違いながら武器振り
<D-Motion:crash&attack>
frame = 5
</D-Motion>

// 武器の止めポーズ
<D-Motion:attack2>
delay = 2
</D-Motion>

// スキルアニメーション
<D-Animation:skill&wait/>

// 透明解除しながら前進アタック
<D-Motion:visible&wait/>

// 戻る
<D-Motion:return/>

